import { useState, useEffect } from "react";
import Spinner from "../components/Spinner";
import Product from "../components/Product";

const Home = () => {
  const API_URL = "https://fakestoreapi.com/products";
  const [loading, setLoading] = useState(false);
  const [posts, setPosts] = useState([]);
  const [Category, setCategory] = useState();
  const [price, setPrice] = useState();
  const [rating,setRating] = useState()

  async function fetchProductData() {
    setLoading(true);

    try{
      const res = await fetch(API_URL);
      const data = await res.json();

      setPosts(data);
    }
    catch(error) {
      console.log("Error aagya ji");
      setPosts([]);
    }
    setLoading(false);
  }
  
  // filteredPosts = posts.filter((post1, post2) => post2?.price - post1?.price)
  // let filteredPosts = posts 
  // if(Category === "men's clothing") {
  //   filteredPosts = posts.filter(post => post?.category === Category)
  // }

  // if(price === "high to low") {
  //   setPrice(filteredPosts = posts.sort((post1, post2) => post2?.price - post1?.price))
  // }

  // if(price === "low to high") {
  //   setPrice(filteredPosts = posts.reverse((post1, post2) => post2?.price - post1?.price))
  // }

  // if(rating === "rating") {
  //   setRating(filteredPosts = posts.reverse((post1, post2) => post2?.rate - post1?.rate))
  // }

  useEffect( () => {
    fetchProductData();
  },[])

  return (
    <div>

      <label htmlFor="filterbutton">Mens Clothing</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"men's clothing"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>

      <label htmlFor="filterbutton">high to low</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"high to low"}
      onChange={(event) => {
        setPrice(event.target.value);
      }}/>

      <label htmlFor="filterbutton">Low to High</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"low to high"}
      onChange={(event) => {
        setPrice(event.target.value);
      }}/>

      <label htmlFor="filterbutton">Rating</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"rating"}
      onChange={(event) => {
        setRating(event.target.value);
      }}/>
      
      {
        loading ? <Spinner />  :
        posts.length > 0 ? 
        (<div className="grid xs:grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 max-w-6xl p-2 space-y min-h-[80vh] mx-auto space-x-5">
          {
            filteredPosts.map( (post) => (
            <Product key = {post.id} post={post}/>
          ) )
          }
        </div>) :
        <div className="flex items-center justify-center"> 
          <p>No Data Found</p>
        </div> 
      }
    </div>
  );
};

export default Home;
