import { createSlice } from "@reduxjs/toolkit";


export const CartSlice = createSlice({
    name:"cart",
    initialState:[],
    reducers:{
        add:(state,_actions) => {
            state.push(action.payload);
        },
        remove:() => {},
    }
});

export const {add, remove} = CartSlice.actions;
export default CartSlice.reducer;
