import React from 'react'
import { useState } from 'react';

const Filter = () => {

    const [posts, setPosts] = useState([]);
    const [Category, setCategory] = useState();
    const [price, setPrice] = useState();

    let filteredPosts = posts 

  
    if(Category === "men's clothing") {
      filteredPosts = posts.filter(post => post?.category === Category)
    }
  
    if(Category === "women's clothing") {
      filteredPosts = posts.filter(post => post?.category === Category)
    }
  
    if(Category === "electronics") {
      filteredPosts = posts.filter(post => post?.category === Category)
    }
  
    if(Category === "jewelery") {
      filteredPosts = posts.filter(post => post?.category === Category)
    }
  
    if(price === "high to low") {
      setPrice(filteredPosts = posts.sort((post1, post2) => post2?.price - post1?.price))
    }

  return (
    <div>
      <label htmlFor="filterbutton">Mens Clothing</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"men's clothing"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>

      <label htmlFor="filterbutton">electronics</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"electronics"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>

      <label htmlFor="filterbutton">women's clothing</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"women's clothing"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>


      <label htmlFor="filterbutton">jewelery</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"jewelery"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>

      <label htmlFor="filterbutton">high to low</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"high to low"}
      onChange={(event) => {
        setPrice(event.target.value);
      }}/>
    </div>
  )
}

export default Filter
