import { useState, useEffect } from "react";
import Spinner from "../components/Spinner";
import Product from "../components/Product";

const Home = () => {
  const API_URL = "https://fakestoreapi.com/products";
  const [loading, setLoading] = useState(false);
  const [posts, setPosts] = useState([]);
  const [Category, setCategory] = useState();
  const [price, setPrice] = useState();
  const [rating,setRating] = useState()
  const [selectedPrice, setSelectedPrice] =useState(999)

  async function fetchProductData() {
    setLoading(true);

    try{
      const res = await fetch(API_URL);
      const data = await res.json();

      setPosts(data);
    }
    catch(error) {
      console.log("Error aagya ji");
      setPosts([]);
    }
    setLoading(false);
  }
  

  let filteredPosts = posts 

  if (selectedPrice && product.price > selectedPrice) {
    return false;
}
  
  if(Category === "men's clothing") {
    filteredPosts = posts.filter(post => post?.category === Category)
  }

  if(Category === "women's clothing") {
    filteredPosts = posts.filter(post => post?.category === Category)
  }

  if(Category === "electronics") {
    filteredPosts = posts.filter(post => post?.category === Category)
  }

  if(Category === "jewelery") {
    filteredPosts = posts.filter(post => post?.category === Category)
  }

  if(price === "high to low") {
    setPrice(filteredPosts = posts.sort((post1, post2) => post2?.price - post1?.price))
  }

  if(price === "low to high") {
    setPrice(filteredPosts = posts.reverse((post1, post2) => post2?.price - post1?.price))
  }

  if(rating === "rating") {
    setRating(filteredPosts = posts.reverse((post1, post2) => post2?.rate - post1?.rate))
  }

  useEffect( () => {
    fetchProductData();
  },[])

  return (
    <div>


        <div>
                <h3>Price</h3>
                <input type="range" 
                min={0} max={999} step="10" 
                value={selectedPrice} 
                onChange={(event)=>setSelectedPrice(event.target.value)} 
                name={'priceRange'}/>
                <p>Price &lt; &#8377;{selectedPrice}</p>
        </div>

      <label htmlFor="filterbutton">Mens Clothing</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"men's clothing"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>

      <label htmlFor="filterbutton">electronics</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"electronics"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>

      <label htmlFor="filterbutton">women's clothing</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"women's clothing"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>


      <label htmlFor="filterbutton">jewelery</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"jewelery"}
      onChange={(event) => {
        setCategory(event.target.value);
      }}/>

      <label htmlFor="filterbutton">high to low</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"high to low"}
      onChange={(event) => {
        setPrice(event.target.value);
      }}/>

      <label htmlFor="filterbutton">Low to High</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"low to high"}
      onChange={(event) => {
        setPrice(event.target.value);
      }}/>

      <label htmlFor="filterbutton">Rating</label>
      <input 
      type="checkbox"
      id="filterbutton"
      value={"rating"}
      onChange={(event) => {
        setRating(event.target.value);
      }}/>
      
      {
        loading ? <Spinner />  :
        posts.length > 0 ? 
        (<div className="grid xs:grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 max-w-6xl p-2 space-y min-h-[80vh] mx-auto space-x-5">
          {
            filteredPosts.map( (post) => (
            <Product key = {post.id} post={post}/>
          ) )
          }
        </div>) :
        <div className="flex flex-col items-center justify-center gap-5 mt-[14rem] font-bold text-xl"> 
          <p>No Data Found</p>
        </div> 
      }
    </div>
  );
};

export default Home;
