import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import CartItem from "../components/CartItem";
import { useEffect, useState } from "react";

const Cart = () => {
  
  const {cart} = useSelector((state) => state)

  const [totalAmount, setTotalAmount] = useState(0)
  useEffect(() => {
    setTotalAmount(cart.reduce ((acc,curr)=> acc + curr.price,0))
  },[cart])

  const description =  `${ description.substring(0,200)}....`;


  return (
    <div>
      {
        cart.length > 0 ? 
        (
          <div className="max-w-6xl p-2 space-y min-h-[80vh] mx-auto space-x-5 flex">
            <div className="w-1/2">
              {
                cart.map((item,index) => (
                  <CartItem key={item.id} item ={item} itemIndex={index} />
                ))
              }
            </div>

            <div className="flex flex-col mt-5">
              <div>
                <div className="text-green-500">
                  Your Cart
                </div>
                <div className="text-3xl font-bold text-green-600 uppercase">
                  Summary 
                </div>
                <p className="mt-3 mb-3">
                  <span>Total item : {cart.length}</span>
                </p>
              </div>

              <div>
                <p className="mb-1"> 
                  Total Amount : <span className="font-bold">${totalAmount}</span>
                </p>
                <button className="px-20 py-2 font-bold text-white bg-green-700 rounded-md">
                  Check Out Now
                </button>
              </div>
            </div>


          </div>
        ) : 
        (
          <div>
            <h1>Your Cart is Empty!</h1>
            <Link to={"/"}>
              <button>Shop Now</button>
            </Link>
          </div>
        )
      }
    </div>
  )
};

export default Cart;
