import { useDispatch, useSelector } from "react-redux";
import { add, remove } from "../redux/Slices/cartSlice";
import { toast } from "react-hot-toast";

const Product = ({post}) => {

  const {cart} = useSelector((state) => state)
  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
    toast.success("item added to cart")
  }

  const removeFromCart = () => {
    dispatch(remove(post.id))
    toast.success("Your Item Remove From Cart")
  }

  return (
    <div className="flex flex-col items-center justify-between
    hover:scale-110 transition duration-300 ease-in rounded gap-3 p-4 mt-10 ml-5 outline">
      <div>
        <p className="text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1">
          {post.title}
        </p>
      </div>
      <div>
        <p className="w-40 text-gray-400 font-normal text-left text-[10px]">
          {post.description.split(" ").slice(0,10).join(" ") + "..."}
        </p>
      </div>
      <div className="h-[180px]">
        <img src= {post.image} alt="" className="h-full w-full"/>
      </div>

      <div className="flex justify-between gap-12">
        <div>
          <p>
            ${post.price}
          </p>
        </div>
        {
          cart.some((p) => p.id === post.id) ?
          (<button 
          onClick={removeFromCart}>
            Remove Item
          </button>) :
          (<button
          onClick={addToCart} >
            Add To Cart
          </button>)
        }  
      </div>
    </div>
  )
};

export default Product;
